public class PfaseShoot : PfaseSr
{
    public void MousClick(int x, int y, Game _g)
    {
        Unit un=_g.IsMap.FindUnit(x, y);
        if(un !=null)
        {
            if (un.w_Player == _g.PlayerNow())
                _g.Sourse = un;
            else
                _g.Target = un;
        }
    }
    public void ActButtonClick(Game _g)
    {
        _g.Shooting();
    }
}

