        return false;
    }

    private void EndPfase()
    {
        foreach (Player p in Players)
        {
            foreach (Unit U in p.PlayersUnit)
            {
                U.EndPfase(this);
            }
        }
        switch(NowPhase)
        {
            case Pfase.Move:
                break;
            case Pfase.Shoot:
                break;
            case Pfase.Charge:
                break;
        }
    }

    private void BeginPfase()
    {
        foreach (Player p in Players)
        {
            foreach (Unit U in p.PlayersUnit)
            {
                U.EndPfase(this);
            }
        }
        switch (NowPhase)
        {
            case Pfase.Move:
                MessageBox.Show("Фаза движения");
                NowPfaseStr = NofingPf;
                break;
            case Pfase.Shoot:
                MessageBox.Show("Фаза стрельбы");
                NowPfaseStr = ShootPf;
                break;
            case Pfase.Charge:
                MessageBox.Show("Фаза атаки");
                NowPfaseStr = NofingPf;
                break;
        }
    }

    public void NextPfase()
    {
        EndPfase();
        NowPhase++;

        if(NowPhase==Pfase.End)
        {
            NowPhase = Pfase.Move;
            NowPlayer++;
            Unit p=Target;
            Target = Sourse;
            Sourse = p;
            if(NowPlayer==2)
            {
                NowPlayer = 0;

                if(Turn==5)
                {
                    if(DiceGen.D6()<=4)
                    {
                        MessageBox.Show("Конец игры");
                    }
                }
                if (Turn == 6)
                {
                    if (DiceGen.D6() <= 5)
                    {
                        MessageBox.Show("Конец игры");
                    }
                }
                if (Turn == 7)
                {
                    MessageBox.Show("Конец игры");
                } 
                Turn++;
                MessageBox.Show("Новый ход");
            }
            else
            {
                MessageBox.Show("Следующий игрок");
            }
        }
        else
        {
            //MessageBox.Show("Новая фаза");
        }
        BeginPfase();
    }

